(175, 1, '2025-06-20 16:00:58', '2025-06-20 19:00:58', '.wpas-login-register {\r\n    display: flex;\r\n    gap: 40px;\r\n    justify-content: center;\r\n    align-items: flex-start;\r\n    flex-wrap: wrap;\r\n}\r\n\r\n.wpas-login-register .wpas-form {\r\n    background-color: #ffffff;\r\n    padding: 30px 25px;\r\n    border-radius: 12px;\r\n    box-shadow: 0 6px 16px rgba(0, 0, 0, 0.1);\r\n    width: 100%;\r\n    max-width: 400px;\r\n    min-height: 520px;\r\n    display: flex;\r\n    flex-direction: column;\r\n}\r\n\r\n.wpas-login-register .wpas-form h3 {\r\n    color: #0E589F;\r\n    margin-bottom: 25px;\r\n    text-align: center;\r\n    font-size: 24px;\r\n}\r\n\r\n/* Agrupa os campos, botão e link com comportamento correto */\r\n.wpas-login-register .wpas-form-group,\r\n.wpas-login-register .wpas-checkbox {\r\n    margin-bottom: 16px;\r\n}\r\n\r\n/* Mantém o espaçamento entre os campos, mas força botão e link para base */\r\n.wpas-login-register .wpas-form > .wpas-btn,\r\n.wpas-login-register .wpas-form > .wpas-forgot-password-link {\r\n    margin-top: auto;\r\n}\r\n\r\n/* Opcional: melhora o espaçamento do link */\r\n.wpas-login-register .wpas-forgot-password-link {\r\n    display: block;\r\n    margin-top: 12px;\r\n    text-align: center;\r\n    color: #083359;\r\n    font-size: 14px;\r\n    text-decoration: none;\r\n    transition: color 0.3s;\r\n}\r\n\r\n.wpas-login-register .wpas-forgot-password-link:hover {\r\n    color: #0E589F;\r\n    text-decoration: underline;\r\n}\r\n\r\n/* Campos */\r\n.wpas-login-register .wpas-form-control {\r\n    width: 100%;\r\n    padding: 12px 14px;\r\n    border: 1px solid #ccd6e0;\r\n    border-radius: 8px;\r\n    outline: none;\r\n    transition: border-color 0.3s, box-shadow 0.3s;\r\n    font-size: 15px;\r\n}\r\n\r\n.wpas-login-register .wpas-form-control:focus {\r\n    border-color: #0E589F;\r\n    box-shadow: 0 0 0 3px rgba(14, 88, 159, 0.2);\r\n}\r\n\r\n/* Checkbox */\r\n.wpas-login-register .wpas-checkbox label {\r\n