(5, 'Adicionar campo de \"tipo\"', '', 'if ( ! defined( \'WPINC\' ) ) {\n    die;\n}\n\nadd_action( \'plugins_loaded\', \'wpas_tipo_chamado_custom_field\' );\nfunction wpas_tipo_chamado_custom_field() {\n    if ( function_exists( \'wpas_add_custom_field\' ) ) {\n        wpas_add_custom_field( \'tipo_chamado\', array(\n            \'title\'       => __( \'Tipo de Chamado\', \'awesome-support\' ),\n            \'field_type\'  => \'select\',\n            \'order\'       => 5,\n            \'required\'    => true,\n            \'default\'     => \'\',\n            \'log\'         => true,\n            \'options\'     => array(\n                \'\'             => __( \'Por favor, selecione\', \'awesome-support\' ),\n                \'sugestao\'     => __( \'Sugestão\', \'awesome-support\' ),\n                \'elogio\'       => __( \'Elogio\', \'awesome-support\' ),\n                \'solicitacao\'  => __( \'Solicitação\', \'awesome-support\' ),\n                \'reclamacao\'   => __( \'Reclamação\', \'awesome-support\' ),\n                \'denuncia\'     => __( \'Denúncia\', \'awesome-support\' ),\n            ),\n            \'select2\' => true,\n        ) );\n    }\n}\n', '', 'global', 10, 1, '2025-06-13 18:31:12', 5, NULL),
(6, 'Painel de login', '', 'add_action( \'login_enqueue_scripts\', \'painel_login\' );\n\nfunction painel_login() { \n	?>\n		<style type=\"text/css\">\n			body.login input[type=password]:focus, body.login input[type=text]:focus {\n				box-shadow: none;\n    			border-bottom: 1px solid #0E589F !important;\n			}\n		\n			body.login .message {\n				box-shadow: none;\n			}\n			\n			.wp-pwd button {\n				color:#000000 !important;\n			}\n		\n			body.login label[for=user_login], body.login label[for=user_pass] {\n				font-style: italic;\n				font-size: 16px;\n			}\n		\n			body.login #user_login, body.login #user_pass {\n				border-bottom: 1px solid #000;\n				border-top: none;\n				border-left: none;\n				border-right: none;\n				background: #fff;\n				border-radius: 0;\n			}\n			/* Trocar cor de fundo */\n			body.login {\n				background:#0E589F;\n			}\n\n			/* Trocar imagem acima dos inputs */\n			#login h1 a, .login h1 a {\n				background-image: url(https://hospitalneurocardio.com.br/tccouvidoriadigital/wp-content/uploads/2025/06/cropped-ouviai-sf.png);\n				background-size: contain;\n				height: 100px;\n				width: 300px;\n				border-radius: 10px;\n									  \n				pointer-events: none;\n			}\n		\n			body.login  #login {\n				background: #fff;\n				position: absolute;\n				top: 50%;\n				left: 50%;\n				transform: translateX(-50%) translateY(-50%);\n				padding: 41px !important;\n				/*box-shadow: 0 0 40px 20px rgba(255,255,255,.6);*/\n				border-radius: 8px;\n			}\n		\n			body.login form {\n				border: none !important;\n				box-shadow: none !important;\n				padding: 0 !important;\n			}\n		\n			body.login .submit {\n				display: block;\n				width: 100%;\n			}\n		\n			body.login .forgetmenot {\n				display: block;\n				width: 100%;\n			}\n		\n			body.login #wp-submit {\n				width: 100%;\n				margin-top: 10px;\n				border: 1px solid #0E589F !important;\n				border-radius: 18px;\n				background-color: #ffffff !important;\n				color: #0E589F !important;\n				padding: 7px;\n				font-weight: 400;\n				letter-spacing: 2px;\n									  \n				transition: .4s ease-in-out;\n			}\n			body.login #wp-submit:hover {\n				background-color: #0E589F !important;\n				color: #ffffff !important;\n									  \n				transition: .4s ease-in-out;\n			}\n		\n			.wp-core-ui .button-primary {\n				background: #0E589F !important;\n				border: #0E589F !important;\n				text-transform: uppercase;\n				font-weight: 900;\n			}\n									  \n			/* Remoção de elementos */\n			body.login .language-switcher{\n				display: none;\n			}\n		\n			body.login a{\n				transition: .4s ease-in-out;\n			}\n			body.login a:hover{\n				color: #0E589F !important;\n			}\n		</style>\n	<?php\n}', '', 'global', 10, 1, '2025-06-17 13:39:27', 4, NULL);
